var artistControllers=angular.module("artistControllers",["ngAnimate"]);artistControllers.controller("ListController",["$scope","$http",function(t,e){e.get("js/data.json").success(function(e){t.artists=e,t.artistOrder="name"})}]),artistControllers.controller("DetailsController",["$scope","$http","$routeParams",function(t,e,r){e.get("js/data.json").success(function(e){t.artists=e,t.whichItem=r.itemId,t.prevItem=r.itemId>0?Number(r.itemId)-1:t.artists.length-1,t.nextItem=r.itemId<t.artists.length-1?Number(r.itemId)+1:0})}]);